/**
 * Devuelve un objeto Box
 * nombre: el nombre del objeto
 * 
 * @param {string} El nombre del box
 */

function getBox(nombre){
  return new Box(nombre)
}

class Box{
  constructor(nombre){
    this.id = Utilities.getUuid()
    this.nombre = nombre     
    this.disable = false   
  }
}


/**
 * Devuelve un objeto Tecnica
 * nombre: el nombre de la técnica
 * movil: el número de movil de la técnica
 * 
 * @param {string} el nombre de la técnica
 * @param {string} el número de móvil de la técnica
 * 
 */
function getTecnica(nombre, movil,color){
  return new Tecnica(nombre,movil,color);
}
class Tecnica{
  constructor(nombre,movil,color=8){
    this.id = Utilities.getUuid() 
    this.nombre= nombre
    this.movil = movil
    this.disable = false
    this.color = color
  }
}

/**
 * Devuelve un objeto Equipo
 * Nombre: el nombre del equipo
 * 
 * @param {string} nombre del Equipo
 */

function getEquipo(nombre){
  return new Equipo(nombre);
}

class Equipo{
  constructor(nombre){
    this.id = Utilities.getUuid() 
    this.nombre = nombre
    this.disable = false
  }
}

/**
 * Devuelve un servicio
 * 
 * @{string} nombre
 */

function getServicio(nombre){
  return new Servicio(nombre)
}
class Servicio{
  constructor (nombre){
    this.id = Utilities.getUuid() 
    this.nombre = nombre
    this.subServicios = []
    this.disable = false
  }

  addSubServicio(subS){
    this.subServicios.push(subS)

  }
}

/**
 * devuelve un subservicio
 * 
 * @param {string} nombre
 * @param [{tecnicas}] Técnicas
 * @param [{box}] Boxes
 * @param [{equipos}] Equipos
 * @param {int} Duración
 */

function getSubServicio(nom,tec,box,equi,dur){
  return new SubServicio(nom,tec,box,equi,dur)
}

class SubServicio{
  constructor(nombre,tecnicas = [],boxes = [],equipos = [],duracion){
    this.id = Utilities.getUuid() 
    this.nombre= nombre
    this.tecnicas = tecnicas
    this.boxes = boxes
    this.equipos = equipos
    this.duracion = duracion
    this.disable = false
  }
}

