/**
 * Recibe una fechaHora y una duración en minutos.
 * Devuelve la fechaHora de finalización
 * (para trabajar en calendar)
 */
function dameFin(inicio,duracion){

  var anio = inicio.getFullYear()
  var mes = inicio.getMonth()
  var dia = inicio.getDate()
  var hora = inicio.getHours()
  var minu = inicio.getMinutes()
  var segu = inicio.getSeconds()
  var mseg = inicio.getMilliseconds()

  minu = minu + duracion

   var fechaFin = new Date(anio,mes,dia,hora,minu,segu,mseg)

   return fechaFin
}

function testDameFin(){
  var str_date = "2021-12-31T23:00"
  var nuevaFecha = new Date(str_date)
  Logger.log(nuevaFecha)
  var fechaFin =dameFin(nuevaFecha,90)
  Logger.log(fechaFin)
}
