        banderaEsta = true
      }
    })
    if(!banderaEsta){
      CalendarApp.createCalendar(nombreAgenda)
    }

    var calParCom = CalendarApp.getCalendarsByName(nombreAgenda)[0]
    calParCom.createEvent(titulo,inicio,fin,descripcion)
}

