function doPost(e){
  Logger.log(JSON.stringify(e))
  if(e.parameter.submit == "Agregar"){

    if(e.parameter.opcion == "AgregarEquipo"){

      var obj_equipo = Clases.getEquipo(e.parameters.r_inputs[0])
      Colecciones.agregarObjeto(obj_equipo,id_equipos)

    }

    if(e.parameter.opcion == "AgregarTecnica"){
      var obj_Tecnica = Clases.getTecnica(e.parameters.r_inputs[0],e.parameters.r_inputs[1],e.parameters.color[0])
      Colecciones.agregarObjeto(obj_Tecnica,id_tecnicas)

    }

    if(e.parameter.opcion == "AgregarBox"){
      var obj_Box = Clases.getTecnica(e.parameters.r_inputs[0])
      Colecciones.agregarObjeto(obj_Box,id_boxes)
    }

    if(e.parameter.opcion == "AgregarServicio"){
      var obj_Ser = Clases.getServicio(e.parameters.r_inputs[0]);
      Colecciones.agregarObjeto(obj_Ser,id_servicios);
    };

    if(e.parameter.opcion == "AgregarSubServicio"){
      if(e.parameter.nombre != ""){
        var b_nombre = e.parameter.nombre

        // Obtengo el id de las técnicas seleccionadas
        var Colid_TecSel = e.parameters.f_tecnicas_sel
        var colTec = []

        if(Colid_TecSel){
          var ColTec = Colecciones.dameColeccion(id_tecnicas)
          for(var i=0 ; i<Colid_TecSel.length ; i++){
            for(var j=0 ; j<ColTec.length ; j++){
              if(Colid_TecSel[i] == ColTec[j].id){
                colTec.push(ColTec[j])
              }
            }
          }
        }


        var Colid_BoxSel = e.parameters.f_boxes_sel
        var colBox = []

        if(Colid_BoxSel){     
          var ColBox = Colecciones.dameColeccion(id_boxes)
          for(var i=0 ; i< Colid_BoxSel.length ; i++){
            for(var j=0 ; j<ColBox.length ; j++){
              if(Colid_BoxSel[i] == ColBox[j].id){
                colBox.push(ColBox[j])
              }
            }
          }
        } 

        var Colid_EquSel = e.parameters.f_equipos_sel
        var colEqu = [] 

        if(Colid_EquSel){     
          var ColEqu = Colecciones.dameColeccion(id_equipos)
            for(var i=0 ; i< Colid_EquSel.length ; i++){
              for(var j=0 ; j<ColEqu.length ; j++){
                if(Colid_EquSel[i] == ColEqu[j].id){
                  colEqu.push(ColEqu[j])
                }
              }
            }        
        }
        var b_duracion = e.parameter.duracion

        var miSubServicio = Clases.getSubServicio(b_nombre,colTec,colBox,colEqu,b_duracion)

        Colecciones.agregarObjeto(miSubServicio,id_subservicios) 
      }
    }


    return menu()
  }
  // ----------  Eliminar -----------------


  //if(e.parameter.submit = "Eliminar"){
  if(e.parameters.submit[0] == "Eliminar"){


    if(e.parameter.opcion == "BorrarEquipo"){
      var b_col_id = e.parameters.f_col_id
      try{
        for(var i=0 ; i<b_col_id.length ; i++){
          Colecciones.eliminarObjeto(b_col_id[i],id_equipos)
        }
      }catch(e){
        Logger.log(e)
      }
      return menu()
    } 

    if(e.parameter.opcion == "BorrarTecnica"){
      var b_col_id = e.parameters.f_col_id
      try{
        for(var i=0 ; i<b_col_id.length ; i++){
          Colecciones.eliminarObjeto(b_col_id[i],id_tecnicas)
        }
      }catch(e){
        Logger.log(e)
      }
      return menu()
    }

    if(e.parameter.opcion == "BorrarBox"){
      var b_col_id = e.parameters.f_col_id
      try{
        for(var i=0 ; i<b_col_id.length ; i++){
          Colecciones.eliminarObjeto(b_col_id[i],id_boxes)
        }
      }catch(e){
        Logger.log(e)
      }   
      return menu()
    }

    if(e.parameter.opcion == "BorrarServicio"){
      var b_col_id = e.parameters.f_col_id;
      try{
        for(var i = 0 ;i<b_col_id.length ; i++){
          Colecciones.eliminarObjeto(b_col_id[i],id_servicios);
        }
      } catch(e){
        Logger.log(e)
      }
      return menu()
    }

    if(e.parameter.opcion == "BorrarSubservicio"){

        Logger.log('... detectó: e.parameter.submit == "Eliminar" ')
        var paraBorrar = e.parameters.f_col_id;
        try{
          for(var i=0 ; i<paraBorrar.length ; i++){
            Colecciones.eliminarObjeto(paraBorrar[i],id_subservicios)
          }
        }catch(e){
          Logger.log(e)
        }

        /*  Prospecto template eliminar.html
        f_action, f_method, f_objetos[], f_submits[] 
        e.parameters.f_col_id[]  e.parameters.submit[ ] */

    }

    return menu()
  }

  if(e.parameter.opcion == "modificar"){
    Logger.log(JSON.stringify(e))
  }


  return menu()
}

