/**
 * Recibe una hoja y un número de columna mayor que cero
 * 
 * @param {obj} Hoja de un libro
 * @param {int} Número de columna de hoja
 */

function getVector(hoja,col) {
  if(col>0){
    var s_tecnicas = hoja.getDataRange().getValues()
    var m_tecnicas = []

    for(var j=0 ; j<s_tecnicas.length ; j++){
      m_tecnicas[j] = JSON.parse(s_tecnicas[j][col-1])
    }

    return m_tecnicas  
  }
}

